import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import styled from "styled-components";

const Vote = () => {
  const history = useHistory();
  const [vote, setVote] = useState("");

  const SubmitVote = async (e) => {
    e.preventDefault();
    const aadhar = localStorage.getItem("aadhar");
    const password = localStorage.getItem("aadhar-password");
    const party = vote;
    if (vote === "") {
      alert("Please Select an option");
      history.push("/vote");
    } else {
      const res = await fetch("/voterecording", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          aadhar,
          password,
          party,
        }),
      });

      if (res.status === 400) {
        alert("Please login to vote");
        history.push("/signin");
      }
      if (res.status === 500) {
        alert("You have already voted. Can not vote again");
        history.push("/");
      }
      if (res.status === 200) {
        alert("Vote recorded successfully");
        history.push("/");
      }
    }
  };

  return (
    <>
      <Container
        style={{
          backgroundImage: 'url("9.jpeg")',
          backgroundPosition: "center",
          backgroundSize: "cover",
        }}
      >
        <Head>
          <Main> Vote for a party</Main>
          <Note style={{ color: "black" }}>
            Note : You will only be allowed to vote once and will not be able to
            change your decision.
          </Note>
        </Head>
        <Parties>
          <form method="POST">
            <table border="1px" width="100%" rules="all">
              <tr>
                <th>Choose</th>
                <th>LOGO</th>
                <th>Party name</th>
              </tr>
              <tr>
                <td>
                  <input
                    type="radio"
                    name="party"
                    id="bjp"
                    value="bjp"
                    onClick={(e) => setVote(e.target.value)}
                  />
                </td>
                <td>
                  <img src="5.svg" alt="" />
                </td>
                <td>
                  <label htmlFor="bjp">BJP</label>
                </td>
              </tr>
              <tr>
                <td>
                  <input
                    type="radio"
                    name="party"
                    id="congress"
                    value="congress"
                    onClick={(e) => setVote(e.target.value)}
                  />
                </td>
                <td>
                  <img src="congress.png" alt="" />
                </td>
                <td>
                  <label htmlFor="congress">Congress</label>{" "}
                </td>
              </tr>
              <tr>
                <td>
                  <input
                    type="radio"
                    name="party"
                    id="aap"
                    value="aap"
                    onClick={(e) => setVote(e.target.value)}
                  />
                </td>
                <td>
                  <img src="aap.png" alt="" />
                </td>
                <td>
                  <label htmlFor="aap">Aam Aadmi Party</label>{" "}
                </td>
              </tr>
              <tr>
                <td>
                  <input
                    type="radio"
                    name="party"
                    id="ncp"
                    value="ncp"
                    onClick={(e) => setVote(e.target.value)}
                  />
                </td>
                <td>
                  <img src="inc.png" alt="" />
                </td>
                <td>
                  <label htmlFor="ncp">National Congress Party</label>{" "}
                </td>
              </tr>
              <tr>
                <td>
                  <input
                    type="radio"
                    name="party"
                    id="inld"
                    value="inld"
                    onClick={(e) => setVote(e.target.value)}
                  />
                </td>
                <td>
                  <img src="inld.png" alt="" />
                </td>
                <td>
                  <label htmlFor="inld">Indian National Lok Dal</label>{" "}
                </td>
              </tr>
            </table>

            <button onClick={SubmitVote}>Vote</button>
          </form>
        </Parties>
      </Container>
    </>
  );
};

export default Vote;
const Container = styled.div`
  height: 79vh;
  width: 100vw;
`;
const Head = styled.div`
  width: 100%;
  height: 10%;
  margin-top: 1rem;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  text-align: center;
  padding: 0 1rem;
  @media all and (max-width: 813px) {
    height: 20%;
  }

  @media all and (max-width: 477px) {
    font-size: 0.9rem;
  }
  @media all and (max-width: 349px) {
    height: 30%;
    font-size: 0.9rem;
  }
`;
const Note = styled.div``;
const Main = styled.div`
  font-size: 1.6rem;
  font-weight: bold;
  color: red;
`;
const Parties = styled.div`
  width: 100%;
  form {
    display: flex;
    justify-content: center;
    flex-direction: column;
    gap: 3rem;
    align-items: center;
    width: 100%;

    button {
      width: 9rem;
      height: 3rem;
      background: rgba(0, 0, 0, 0.2);
      border-radius: 4px;
      cursor: pointer;
      border: none;
      outline: none;
    }
  }

  table {
    font-family: arial, sans-serif;
    width: 70%;
  }

  td,
  th {
    font-family: "Gill Sans", sans-serif;
    border: 1px solid black;
    text-align: left;
    padding: 0px 1rem;

    img {
      width: 4.3rem;
      height: 4rem;
    }
  }
`;
