import React from "react";
import { NavLink, useHistory } from "react-router-dom";
import styled from "styled-components";

const Navbar = () => {
  const history = useHistory();

  const logout = async (e) => {
    e.preventDefault();

    const logo = await fetch("/logout", {
      method: "GET",
    });
    if (logo.status === 200) {
      alert("Successfully logged out");
    }
    if (logo.status === 400) {
      alert("Please login to log out :)");
      history.push("/signin");
    }
  };

  return (
    <Container>
      <MidCont>
        <img src="middle.png" alt="" />
      </MidCont>
      <RightSide>
        <ul>
          <li className="home">
            <NavLink exact activeClassName="active" to="/">
              Home
            </NavLink>
          </li>
          <li className="vote">
            <NavLink exact activeClassName="active" to="/vote">
              Vote
            </NavLink>
          </li>
          <li className="register">
            <NavLink exact activeClassName="active" to="/signup">
              Register
            </NavLink>
          </li>
          <li className="login">
            <NavLink exact activeClassName="active" to="/signin">
              Login
            </NavLink>
          </li>
          <li className="logout">
            <NavLink
              exact
              activeClassName="active"
              to="/signout"
              onClick={logout}
            >
              Logout
            </NavLink>
          </li>
        </ul>
      </RightSide>
    </Container>
  );
};

export default Navbar;

const Container = styled.div`
  font-family: "Roboto Mono", monospace;
  height: 5rem;
  width: 100vw;
  border-bottom: 2px solid black;
  display: flex;
  justify-content: space-between;
  align-items: center;
  box-shadow: 1px 0px 6px black;

  @media all and (max-width: 800px) {
    height: 10rem;
    flex-direction: column;
    padding-bottom: 1rem;
    justify-content: center;
  }
`;

const MidCont = styled.div`
  height: 100%;
  display: flex;
  overflow: hidden;
  justify-content: center;
  align-items: center;
  font-size: 1.5rem;

  img {
    width: 25rem;
  }
`;

const RightSide = styled.div`
  font-size: 1.2rem;
  margin-right: 2rem;
  display: flex;
  gap: 1.5rem;

  ul {
    margin: 0;
    padding: 0;
    display: flex;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
  }

  ul li {
    list-style: none;
    margin: 0 15px;
  }

  ul li a {
    position: relative;
    display: block;
    width: 60px;
    height: 60px;
    text-align: center;
    line-height: 63px;
    background: #333;
    border-radius: 50%;
    font-size: 30px;
    color: #666;
    transition: 0.5s;
  }

  ul li a::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    border-radius: 50%;
    background: #ffee10;
    transition: 0.5s;
    transform: scale(0.9);
    z-index: -1;
  }

  ul li a:hover::before {
    transform: scale(1.1);
    box-shadow: 0 0 15px #ffee10;
  }

  ul li a:hover {
    color: #ffee10;
    box-shadow: 0 0 5px #ffee10;
    text-shadow: 0 0 5px #ffee10;
  }

  @media all and (max-width: 435px) {
    font-size: 1rem;
  }

  @media (max-width: 800px) {
    .links {
      display: none;
    }
  }

  div {
    cursor: pointer;
    height: 2rem;
  }

  a {
    height: 100%;
    text-decoration: none;
  }

  .nav-toggle {
    font-size: 1.5rem;
    color: var(--clr-primary-5);
    background: transparent;
    border-color: transparent;
    transition: var(--transition);
    cursor: pointer;
  }

  .nav-toggle:hover {
    color: var(--clr-primary-1);
    transform: rotate(90deg);
  }

  @media all and (min-width: 800px) {
    .nav-toggle {
      display: none;
    }
  }
`;
